<?php/** * n8n Integration API - Enrich Portfolio *  * Receives enriched holdings data from n8n and updates portfolio.json. * Triggers recalculation of all metrics (P&L, allocations, drift). * Protected by HMAC-SHA256 authentication. *  * @endpoint POST /api/n8n/enrich.php * @auth HMAC-SHA256 (header: X-Webhook-Signature) */header('Content-Type: application/json');require_once __DIR__ . '/../../lib/PortfolioManager.php';require_once __DIR__ . '/../../lib/HMACValidator.php';// CORS headers for n8nheader('Access-Control-Allow-Origin: *');header('Access-Control-Allow-Methods: POST, OPTIONS');header('Access-Control-Allow-Headers: Content-Type, X-Webhook-Signature');if ($_SERVER['REQUEST_METHOD'] === 'OPTIONS') {    http_response_code(200);    exit;}// Only allow POSTif ($_SERVER['REQUEST_METHOD'] !== 'POST') {    http_response_code(405);    echo json_encode(['error' => 'Method not allowed']);    exit;}try {    // Get raw payload    $rawPayload = file_get_contents('php://input');    $signature = $_SERVER['HTTP_X_WEBHOOK_SIGNATURE'] ?? '';        // Validate HMAC    if (!HMACValidator::validate($rawPayload, $signature)) {        http_response_code(401);        echo json_encode([            'success' => false,            'error' => 'Invalid HMAC signature'        ]);        exit;    }        // Parse JSON    $data = json_decode($rawPayload, true);    if (json_last_error() !== JSON_ERROR_NONE) {        http_response_code(400);        echo json_encode([            'success' => false,            'error' => 'Invalid JSON',            'details' => json_last_error_msg()        ]);        exit;    }        // Validate required fields    if (!isset($data['holdings']) || !is_array($data['holdings'])) {        http_response_code(400);        echo json_encode([            'success' => false,            'error' => 'Missing or invalid holdings array'        ]);        exit;    }        // Log workflow execution    $workflowId = $data['workflow_id'] ?? 'unknown';    $timestamp = $data['timestamp'] ?? date('c');    error_log("[n8n/enrich] Received enrichment from workflow: $workflowId at $timestamp");        // Load portfolio    $portfolioManager = new PortfolioManager();    $updatedCount = 0;        // Update each holding    foreach ($data['holdings'] as $enrichedHolding) {        $isin = $enrichedHolding['isin'] ?? null;                if (!$isin) {            error_log("[n8n/enrich] Warning: Holding without ISIN, skipping");            continue;        }                // Find holding in portfolio        $holding = $portfolioManager->getHoldingByIsin($isin);                if (!$holding) {            error_log("[n8n/enrich] Warning: ISIN $isin not found in portfolio");            continue;        }                // Update fields if provided        $updates = [];                if (isset($enrichedHolding['current_price'])) {            $updates['current_price'] = (float) $enrichedHolding['current_price'];        }                if (isset($enrichedHolding['asset_class']) && $enrichedHolding['asset_class'] !== 'Unknown') {            $updates['asset_class'] = $enrichedHolding['asset_class'];        }                if (isset($enrichedHolding['sector']) && $enrichedHolding['sector'] !== 'Unknown') {            $updates['sector'] = $enrichedHolding['sector'];        }                if (isset($enrichedHolding['expense_ratio'])) {            $updates['expense_ratio'] = (float) $enrichedHolding['expense_ratio'];        }                if (isset($enrichedHolding['dividend_yield'])) {            $updates['dividend_yield'] = (float) $enrichedHolding['dividend_yield'];        }                // Apply updates        if (!empty($updates)) {            $portfolioManager->updateHolding($isin, $updates);            $updatedCount++;            error_log("[n8n/enrich] Updated holding $isin: " . json_encode($updates));        }    }        // Recalculate all metrics (P&L, allocations, drift)    $portfolioManager->recalculateMetrics();        // Save portfolio    $portfolioManager->save();        // Get updated metadata    $updatedData = $portfolioManager->getData();    $metadata = $updatedData['metadata'];        // Log success    error_log("[n8n/enrich] Successfully enriched $updatedCount holdings");        // Response    http_response_code(200);    echo json_encode([        'success' => true,        'message' => 'Portfolio enriched successfully',        'updated_holdings' => $updatedCount,        'metadata' => [            'total_value' => $metadata['total_value'] ?? 0,            'unrealized_pnl' => $metadata['unrealized_pnl'] ?? 0,            'unrealized_pnl_pct' => $metadata['unrealized_pnl_pct'] ?? 0,            'holdings_count' => $metadata['holdings_count'] ?? 0,            'last_update' => $metadata['last_update'] ?? date('c')        ]    ], JSON_PRETTY_PRINT);    } catch (Exception $e) {    error_log("[n8n/enrich] Error: " . $e->getMessage());    error_log("[n8n/enrich] Trace: " . $e->getTraceAsString());        http_response_code(500);    echo json_encode([        'success' => false,        'error' => 'Internal server error',        'message' => $e->getMessage()    ]);}