/**
 * PREPARE CONTEXT REQUEST
 *
 * Normalizza l'input proveniente dal trigger (Webhook o Schedule)
 * e costruisce il body JSON da inviare a /api/n8n/technical-context.php.
 *
 * Output JSON:
 * {
 *   "portfolio_id": 1,
 *   "isins": [...opzionale...]
 * }
 */

const input = $json;

// 1) Recupero APP_BASE_URL se presente da nodi precedenti (es. Config Variables)
const appBaseUrl =
  input.APP_BASE_URL ||
  $env.APP_BASE_URL || // se usi variables globali n8n
  "https://portfolio.newwave-media.it";

// 2) Portfolio ID
// - Se arriva dal webhook, usalo
// - Altrimenti fallback a 1
const portfolioId = input.portfolio_id || 1;

// 3) ISINs opzionali
// - Se arrivano dal webhook, validiamo che siano array di stringhe
let isins = [];
if (Array.isArray(input.isins)) {
  isins = input.isins.filter(x => typeof x === "string" && x.trim().length > 0);
}

// 4) Output finale
return [
  {
    json: {
      APP_BASE_URL: appBaseUrl,
      portfolio_id: portfolioId,
      isins: isins.length > 0 ? isins : undefined
    }
  }
];

