/**
 * PARSE TECHNICAL CONTEXT
 *
 * Input: output dell'HTTP Request "Get Technical Context"
 * (nel tuo caso è un array con un solo oggetto).
 *
 * Output: un singolo item con struttura normalizzata:
 * {
 *   portfolio_id,
 *   as_of,
 *   holdings: [
 *     {
 *       ticker, isin, name, sector, asset_class,
 *       price,
 *       indicators: { ... },
 *       derived: { trend_ema50_ema200, momentum_ema9_ema21, ... }
 *     }
 *   ]
 * }
 */

// Gestione root: può essere o l'oggetto, o [ oggetto ]
let root = $json;
if (Array.isArray(root)) {
  if (root.length === 0) {
    throw new Error("Empty response from technical-context.php");
  }
  root = root[0];
}

if (!root.success) {
  throw new Error("technical-context.php returned success = false");
}

const portfolioId = root.portfolio_id || 1;
const holdings = Array.isArray(root.holdings) ? root.holdings : [];

// helper per arrotondare numeri in modo consistente
function roundNum(value, decimals = 2) {
  if (typeof value !== "number" || isNaN(value)) return null;
  return parseFloat(value.toFixed(decimals));
}

const normalizedHoldings = holdings.map((h) => {
  const price = typeof h.price === "number" ? h.price : null;
  const ema9 = typeof h.ema9 === "number" ? h.ema9 : null;
  const ema21 = typeof h.ema21 === "number" ? h.ema21 : null;
  const ema50 = typeof h.ema50 === "number" ? h.ema50 : null;
  const ema200 = typeof h.ema200 === "number" ? h.ema200 : null;

  // Trend medio periodo: EMA50 vs EMA200
  let trend = "unknown";
  if (ema50 !== null && ema200 !== null) {
    if (ema50 > ema200 * 1.01) trend = "bullish";
    else if (ema50 < ema200 * 0.99) trend = "bearish";
    else trend = "neutral";
  }

  // Momentum breve: EMA9 vs EMA21
  let momentum = "unknown";
  if (ema9 !== null && ema21 !== null) {
    if (ema9 > ema21 * 1.005) momentum = "bullish";
    else if (ema9 < ema21 * 0.995) momentum = "bearish";
    else momentum = "neutral";
  }

  // Vicinanza a 52w high/low (booleani soft)
  let near52wHigh = null;
  let near52wLow = null;
  if (price !== null && typeof h.fifty_two_week_high === "number") {
    near52wHigh = price >= h.fifty_two_week_high * 0.97; // entro ~3% dal max
  }
  if (price !== null && typeof h.fifty_two_week_low === "number") {
    near52wLow = price <= h.fifty_two_week_low * 1.03; // entro ~3% dal min
  }

  return {
    ticker: h.ticker || null,
    isin: h.isin || null,
    name: h.name || null,
    sector: h.sector || null,
    asset_class: h.asset_class || null,

    price: roundNum(price, 4),

    indicators: {
      ema9: roundNum(ema9, 4),
      ema21: roundNum(ema21, 4),
      ema50: roundNum(ema50, 4),
      ema200: roundNum(ema200, 4),

      rsi14: roundNum(h.rsi14, 2),

      atr14: roundNum(h.atr14, 4),
      atr14_pct: roundNum(h.atr14_pct, 2),

      hist_vol_30d: roundNum(h.hist_vol_30d, 2),
      hist_vol_90d: roundNum(h.hist_vol_90d, 2),

      range_1m_percentile: roundNum(h.range_1m_percentile, 2),
      range_3m_percentile: roundNum(h.range_3m_percentile, 2),
      range_1y_percentile: roundNum(h.range_1y_percentile, 2),

      bb_percent_b: roundNum(h.bb_percent_b, 4),

      macd_value: roundNum(h.macd_value, 4),
      macd_signal: roundNum(h.macd_signal, 4),
      macd_hist: roundNum(h.macd_hist, 4),

      ytd_change_percent: roundNum(h.ytd_change_percent, 2),
      one_month_change_percent: roundNum(h.one_month_change_percent, 2),
      three_month_change_percent: roundNum(h.three_month_change_percent, 2),
      one_year_change_percent: roundNum(h.one_year_change_percent, 2),

      fifty_two_week_high: roundNum(h.fifty_two_week_high, 4),
      fifty_two_week_low: roundNum(h.fifty_two_week_low, 4)
    },

    derived: {
      trend_ema50_ema200: trend,
      momentum_ema9_ema21: momentum,
      near_52w_high: near52wHigh,
      near_52w_low: near52wLow
    }
  };
});

return [
  {
    json: {
      portfolio_id: portfolioId,
      as_of: new Date().toISOString(),
      holdings: normalizedHoldings
    }
  }
];

/**
 * BUILD LLM PROMPT
 * Usa il contesto normalizzato da Parse Technical Context
 * e costruisce system_prompt + user_prompt come stringhe.
 */

const ctx = $json; // { portfolio_id, as_of, holdings[...] }

const systemPrompt = `
Sei un analista tecnico che analizza un portafoglio di ETF.
- NON dai mai consigli diretti di acquisto o vendita.
- NON parli di apertura ordini o automazione.
- Ti limiti a descrivere trend, momentum, volatilità e posizionamento nel range.
- Rispondi SOLO in JSON valido, senza testo extra.
`.trim();

const userPrompt = `
Di seguito trovi i dati di un portafoglio ETF, con indicatori tecnici per ciascun strumento.

Restituisci un JSON con questa struttura:
{
  "portfolio": {
    "insight_text": "... breve sintesi in italiano ...",
    "insight_json": { ... dettagli strutturati ... }
  },
  "instruments": [
    {
      "isin": "...",
      "insight_text": "... breve sintesi per questo strumento ...",
      "insight_json": {
        "trend": "bullish|neutral|bearish",
        "risk": "low|medium|high",
        "flags": ["overbought", "high_volatility", "..."],
        "notes": "..."
      }
    }
  ]
}

Dati tecnici del portafoglio (JSON):
${JSON.stringify(ctx, null, 2)}
`.trim();

return [
  {
    json: {
      system_prompt: systemPrompt,
      user_prompt: userPrompt,
      model: "gpt-4.1-mini" // o quello che userai
    }
  }
];

/**
 * PARSE LLM OUTPUT (Final v2 - Structured Scores & Signals)
 *
 * Input:
 *   - output del nodo "Message a model" (OpenAI)
 *   - contesto tecnico dal nodo "Parse Technical Context"
 *
 * Output:
 *   payload completo per /api/n8n/ai_insights.php
 */

const ctx = $node["Parse Technical Context"].json;

// ----------------------------
// 1) Estrarre testo dal nodo AI
// ----------------------------
let raw = null;

if (Array.isArray($json.output) && $json.output.length > 0) {
  const block = $json.output[0];
  if (Array.isArray(block.content) && block.content.length > 0) {
    raw = block.content[0].text;
  }
}

if (typeof raw !== "string") {
  throw new Error("AI output is not a string: expected output[0].content[0].text");
}

// ----------------------------
// 2) Pulizia formattazione
// ----------------------------
let cleaned = raw.trim();

if (cleaned.startsWith("```")) {
  cleaned = cleaned
    .replace(/```json/gi, "")
    .replace(/```/g, "")
    .trim();
}

// ----------------------------
// 3) Parse JSON
// ----------------------------
let parsed;
try {
  parsed = JSON.parse(cleaned);
} catch (err) {
  throw new Error(
    "Failed to parse JSON from LLM: " +
      err.message +
      "\nRaw:\n" +
      cleaned
  );
}

// ----------------------------
// 4) Normalizzazione dei campi
// ----------------------------

// --- Portfolio block ---
const portfolioBlock = parsed.portfolio || {};
const portfolioInsightJSON = portfolioBlock.insight_json || {};
const portfolioScores = portfolioBlock.scores || {};
const portfolioText = portfolioBlock.insight_text || "";

// --- Instruments block ---
const instruments = Array.isArray(parsed.instruments)
  ? parsed.instruments
  : [];

// --- Timestamp MySQL ---
const now = new Date();
const mysqlTimestamp = now.toISOString().slice(0, 19).replace("T", " ");

// ----------------------------
// 5) Preparazione payload per PHP
// ----------------------------
const payload = {
  model: "gpt-5-mini",          // puoi anche prenderlo dinamico
  generated_at: mysqlTimestamp,

  portfolio: {
    raw_input_snapshot: ctx,
    insight_json: {
      scores: portfolioScores,
      ...portfolioInsightJSON,
    },
    insight_text: portfolioText
  },

  instruments: instruments
    .filter(i => i && (i.isin || i.ticker))
    .map(i => ({
      isin: i.isin || null,
      ticker: i.ticker || null,
      generated_at: mysqlTimestamp,
      raw_input_snapshot: null,

      insight_json: {
        scores: i.scores || {},
        signals: i.signals || {},
        levels: i.levels || {},
        ...(i.insight_json || {})
      },

      insight_text: i.insight_text || ""
    }))
};

// ----------------------------
// 6) Output verso HTTP Request finale
// ----------------------------
return [{ json: payload }];

