// Extract holdings array from API response
const holdings = $input.first().json.holdings || [];
const config = $input.all()[0].json; // Get config from first node

return holdings.map(h => ({
  json: {
    ...h,
    APP_BASE_URL: config.APP_BASE_URL,
    WEBHOOK_SECRET: config.WEBHOOK_SECRET
  }
}));

// Parse Yahoo Finance response and enrich holding
const holding = $input.first().json;
const yahooData = $input.last().json;

let enriched = {
  isin: holding.isin,
  current_price: holding.current_price,
  APP_BASE_URL: holding.APP_BASE_URL,
  WEBHOOK_SECRET: holding.WEBHOOK_SECRET
};

if (yahooData && yahooData.chart && yahooData.chart.result && yahooData.chart.result[0]) {
  const result = yahooData.chart.result[0];
  const price = result.meta?.regularMarketPrice;

  if (price && price > 0) {
    enriched.current_price = price;
  }
}

const name = holding.name.toLowerCase();
const instrumentType = holding.instrument_type;

if (name.includes('gold') || name.includes('silver') || instrumentType === 'ETC') {
  enriched.asset_class = 'Commodity';
  enriched.sector = name.includes('gold') ? 'Gold' : 'Precious Metals';
}
else if (instrumentType === 'ETF') {
  enriched.asset_class = 'Equity';

  if (name.includes('dividend') || name.includes('aristocrat')) {
    enriched.sector = 'Dividend';
  } else if (name.includes('world') || name.includes('global')) {
    enriched.sector = 'Global';
  } else if (name.includes('u.s.') || name.includes('s&p')) {
    enriched.sector = 'USA';
  } else {
    enriched.sector = 'Mixed';
  }
}
else if (instrumentType === 'Fondo') {
  enriched.asset_class = 'Equity';

  if (name.includes('biotech')) {
    enriched.sector = 'Healthcare';
  } else if (name.includes('robot') || name.includes('tech')) {
    enriched.sector = 'Technology';
  } else if (name.includes('dividend')) {
    enriched.sector = 'Dividend';
  } else {
    enriched.sector = 'Active Fund';
  }
}

return [{ json: enriched }];

// Aggregate all enriched holdings
const items = $input.all();

// Get config from first item
const firstItem = items[0].json;
const appBaseUrl = firstItem.APP_BASE_URL;
const webhookSecret = firstItem.WEBHOOK_SECRET;

// Extract only enrichment data (remove config fields)
const holdings = items.map(item => {
  const { APP_BASE_URL, WEBHOOK_SECRET, ...enrichment } = item.json;
  return enrichment;
});

const payload = {
  workflow_id: 'portfolio_enrichment_v1',
  timestamp: new Date().toISOString(),
  holdings: holdings
};

const crypto = require('crypto');
const payloadStr = JSON.stringify(payload);
const hmac = crypto.createHmac('sha256', webhookSecret).update(payloadStr).digest('hex');

return [{
  json: {
    APP_BASE_URL: appBaseUrl,
    payload: payload,
    signature: `sha256=${hmac}`,
    payloadStr: payloadStr
  }
}];
